INSERT INTO `province` (`id`, `name`, `description`, `location`, `phone`, `website`, `image`, `category`) VALUES
(1, 'วัดพระธาตุช่อแฮ่', 'เมื่อมาถึงเมืองแพร่นั้นต้องแวะมา วัดพระธาตุช่อแฮ่ วัดศักดิ์สิทธิ์เก่าแก่ คู่บ้านคู่เมืองจังหวัดแพร่และเป็นวัดพระธาตุประจำปีเกิดของผู้ที่เกิดปีขาล บุคคลใดที่มาเที่ยวแพร่แล้วจะต้องมานมัสการพระธาตุช่อแฮ เพื่อเป็นศิริมงคล มีคำกล่าวว่า ถ้ามาเที่ยวแพร่ แต่ไม่ได้มานมัสการพระธาตุช่อแฮเหมือนไม่ได้มาจังหวัดแพร่', ' 1 หมู่ 11 ตำบล ช่อแฮ อำเภอเมืองแพร่ จังหวัดแพร่ 54000', '054-599-209', 'http://localhost/Phrae/category/travelprogram/chorhae.php', 'uploads/chorhae0.jpg', 'province'),
(2, 'ถนนลอยฟ้าแพร่', 'ถนนลอยฟ้าแพร่ หมายเลข 1343 ที่เที่ยวแพร่ ที่หลายคนเรียกว่าถนนเทเลทับบี้ เป็นถนนที่มีทิวเขาเขียวสวยงามเป็นริ้วลายที่สวยงาม คล้ายถนนเส้นบ่อเส้นสันติสุข บ่อเกลือ น่าน ถนนเส้นนี้ตั้งอยู่หลังพระธาตุปูแจ ทอดยาวหลายกิโล ระหว่างทางมีทั้งที่พัก ลานกางเต้นท์ ร้านกาแฟให้แวะ สำหรับจุดที่สามารถมองเห็นวิวทิวเขาได้สวยงามที่สุด พิกัดจะอยู่ก่อนถึงไร่ลุงลอน ใครแวะไปเที่ยวน่านซึ่งต้องผ่านจังหวัดแพร่อยากมาถ่ายรูปถนนลอยฟ้าแพร่ ให้ปักหมุดมาที่ไร่ลุงลอน', ' ตำบล บ้านเวียง อำเภอ ร้องกวาง จังหวัดแพร่ 54000', '-.', 'http://localhost/Phrae/category/travelprogram/skyway.php', 'uploads/skyway.jpg', 'province'),
(3, 'วัดสวรรคนิเวศ', 'วัดสวรรคนิเวศ วัดสวยกลางเมืองแพร่ มีถ้ำพญานาคราช ตามรอยบารมีพญานาคาเมืองโกศัยนคร ด้านในถ้ำตกเเต่งอย่างสวยงาม ประดับด้วยไฟหลากสี งดงามด้วยรูปปั้นองค์พญานาคต่างๆ องค์พระพุทธรูป รวมทั้งรูปพระเกจิอาจาร์ยชื่อดัง ด้านในบรรยากาศเงียบสงบ เป็นที่เที่ยวในเมืองแพร่ ที่สร้างได้สวยงามมีมนต์ขลังมากด้านบนของถ้ำยังมีรูปปั้นเจ้าแม่กวนอิม และพระพุทธมหาเศรษฐีนวโกฏิ ประทับด้านบนพระราหูองค์ใหญ่ที่สุดในโลก', 'หมู่ 4 ตำบลทุ่งกวาว อำเภอเมืองแพร่ จังหวัดแพร่ 54000', '054-061-126', 'http://localhost/Phrae/category/travelprogram/watsawanniwet.php', 'uploads/watsawanniwet.jpg', 'province'),
(4, ' วนอุทยานแพะเมืองผี', 'แพะเมืองผี ที่เที่ยวแพร่ ที่สร้างขึ้นชื่อของแพร่มาช้านาน เกิดจากสภาพภูมิประเทศซึ่งเป็นดิน และหินทรายถูกกัดเซาะตามธรรมชาติเป็นรูปร่างลักษณะต่างๆ มีความสวยงามด้านธรณีวิทยา หน้าผา เสาดิน โดยจักเป็นเส้นทางศึกษาธรรมชาติให้นักท่องเที่ยวทั่วไป ได้เข้ามาศึกษาสภาพป่า ลักษณะทางธรณีวิทยา เพื่อเป็นการส่งเสริม ให้เกิดความรู้และความเข้าใจเกี่ยวกับสภาพพื้นที่วนอุทยานมากยิ่งขึ้น', 'ตำบล น้ำชำ อำเภอเมืองแพร่ จังหวัดแพร่  54000', '-.', 'http://localhost/Phrae/category/travelprogram/phaemuangphi.php', 'uploads/phaemuangphi.jpg', 'province'),
(5, 'คุ้มวิชัยราชา ', 'คุ้มวิชัยราชา เรือนไม้โบราณที่เป็นสถาปัตยกรรมอันลำค่าทางประวัติศาตร์ที่มีอายุกว่า 100 ปี ตั้งอยู่ในเขตกำแพงเมืองเก่าแพร่เป็นเรือนไม้ที่ผสมผสานระหว่างเรือนไม้แบบมนิลาและเรือนขนมปังขิงร่วมกับสถาปัตยกรรมล้านนา ประดับตกตกแต่งลวดลายด้วยไม้ฉลุที่สวยงามอ่อนช้อยอยู่ทั่วตัวอาคาร นับเป็นที่เที่ยวแพร่ในบ้านไม่เก่าที่มีความงดงามที่หาชมได้ยากในปัจจุบัน', 'ถนน วิชัยราชา ตำบล ในเวียง อำเภอเมืองแพร่ จังหวัดแพร่ 54000', '096-080-2380', 'http://localhost/Phrae/category/travelprogram/wichairacha.php', 'uploads/wichairacha.jpg', 'province'),
(6, 'คุ้มเจ้าหลวงเมืองแพร่', 'คุ้มเจ้าหลวง ตั้งอยู่ที่ในอำเภอเมืองแพร่ คุ้มแห่งนี้เป็นอาคารโอ่โถง มีประตู หน้าต่างทั้งหมด 72 บาน งดงามด้วยลวดลายฉลุไม้อยู่ด้านบนปั้นลม และชายคาน้ำ เป็นสถาปัตยกรรมสมัยรัชกาลที่ 5 ยุคต้น ซึ่งมีรูปทรงเป็นแบบสถาปัตยกรรมไทยผสมยุโรป ถือเป็นอีกหนึ่งมนต์เสน่ห์แห่งเรือนเก่า ที่ไม่ควรพลาดแวะมาเที่ยวแพร่ ชมสถาปัตยกรรมเก่าแก่แห่งนี้', ' 4 ถนน ซอย คุ้มเดิม ตำบล ในเวียง อำเภอเมืองแพร่ จังหวัดแพร่ 54000', '054-524-158', 'http://localhost/Phrae/category/travelprogram/khumjaoluang.php', 'uploads/khumjaoluang.jpg', 'province'),
(7, 'วัดจอมสวรรค์', 'วัดจอมสวรรค์ วัดเก่าแก่ที่สถานที่เที่ยวแพร่ ที่สร้างด้วยศิลปะพุกาม (พม่า) มีศิลปวัตถุและโบราณวัตถุที่สวยงามและทรงคุณค่า วัดนี้พิเศษ คือ มีเพียงอุโบสถไม้หลังเดียวซึ่งเป็นทั้งโบสถวิหารและกุฏิ อยู่ในอาคารเดียวกัน ตัวอาคารสร้างด้วยไม้สักทั้งหลัง มีหลังคาเล็กใหญ่ลดหลั่นกันเป็นชั้นรวม 9 ชั้น ปัจจุบันกรมศิลปากรได้ทำการขึ้นทะเบียนโบราณสถานวัดจอมสวรรค์ไว้เป็นสมบัติของชาติ', 'ตำบล ทุ่งกวาว อำเภอเมืองแพร่ จังหวัดแพร่ 54000', '-.', 'http://localhost/Phrae/category/travelprogram/chomsawan.php', 'uploads/chomsawan.jpg', 'province'),
(8, 'วัดนาคูหา ', 'วัดนาคูหา ตั้งอยู่ไม่ไกลจากตัวเมืองแพร่ ใช้เวลาเดินทางเพียง 30 นาที สามารถเที่ยวได้โดยใช้เวลาเพียงไม่กี่ชั่วโมง วัดนาคูหา ตั้งอยู่ในหมู่บ้านนาคูหา ท่ามกลางขุนเขาและธรรมชาติอันแสนบริสุทธิ์ โดยมีพระพุทธรูปพระเจ้าทันใจขนาดใหญ่ประดิษฐานโดดเด่นกลางทุ่งนา มีสะพานไม้ไผ่ทอดยาวจากองค์พระพาดผ่านทุ่งนา สำหรับเดินเล่นรับลมชมวิว เป็นที่เที่ยวแพร่ ต้องแวะมาชมวิว ที่มองเห็นธรรมชาติรอบวัดได้อย่างสวยงาม', 'ตำบล สวนเขื่อน อำเภอเมืองแพร่ จังหวัดแพร่ 54000', '-.', 'http://localhost/Phrae/category/travelprogram/nakhuha.php', 'uploads/nakhuha.jpg', 'province'),
(9, 'วัดพระธาตุสุโทนมงคลคีรี', 'วัดพระธาตุสุโทนมงคลคีรี ที่เที่ยวแพร่ ที่มีจุดเด่น คือเป็นวัดที่รวบรวมศิลปกรรมล้านนาประยุกต์ที่สวยงามมากที่สุดแห่งหนึ่ง ในบริเวณวัดมีเจดีย์ทรงล้านนากว่า 30 องค์ ด้านหน้าบันไดทาง ขึ้นทิศตะวันออกมีรูปปั้นสิงห์ขนาดใหญ่และพระนอนองค์ใหญ่โดดเด่นอยู่ด้านหน้าวัด นอกจากนี้ยังมีอาคารพิพิธภัณฑ์ไม้สักทรงล้านนา ที่เก็บรวบรวมเรื่องราวของล้านนาและเมืองแพร่ จัดแสดงเครื่องใช้อาวุธของนักรบโบราณ รวมทั้งภาพถ่ายของเจ้านายฝ่ายเหนือและ ภาพเหตุการณ์ต่างๆ ในล้านนา', '10 ซอย พระร่วง 2 ตำบล ในเวียง อำเภอเมืองแพร่ แพร่ 54000', '', 'https://web.facebook.com/Watphrathatchohae/?locale=th_TH&_rdc=1&_rdr', 'uploads/don.jpg', 'province');
