INSERT INTO `travelprogram` (`travelprogram_id`, `name`, `description`, `main_image`, `subtopic`, `location`, `phone`, `opening_time`, `website`, `coordinates`, `map`, `page`, `category`) VALUES
(1, 'วัดพระธาตุช่อแฮ พระอารามหลวง', 'มาเมืองแพร่ ต้องมานมัสการ...พระธาตุช่อแฮ วัดพระธาตุช่อแฮ พระอารามหลวง วัดศักดิ์สิทธิ์เก่าแก่คู่บ้านคู่เมืองแพร่ และเป็นวัดพระธาตุประจำปีเกิดของผู้ที่เกิดปีขาล ถ้ามาเที่ยวจังหวัดแพร่ แต่ไม่ได้มานมัสการพระธาตุช่อแฮเหมือนไม่ได้มาจังหวัดแพร่  เป็นปูชนียสถานอันศักดิ์สิทธิ์คู่บ้านคู่เมืองแพร่มาแต่โบราณ และเป็นพระธาตุประจำปีเกิดของคนเกิดปีขาล มีงานนมัสการพระธาตุในราวเดือน 4 (เดือนมกราคม) ของทุกปี อยู่ริมถนนช่อแฮ ตำบลช่อแฮ เป็นปูชนียสถานคู่บ้านคู่เมืองแพร่ และเป็นพระธาตุประจำปีขาล ตามตำนานระบุว่า สร้างเมื่อ พ.ศ. 1879-1881 สมัยพระมหาธรรมราชาธิราช (ลิไท) ', '1745163338_watchorhae.jpg', 'ข้อมูลติดต่อ', ' 1 หมู่ 11 ตำบล ช่อแฮ อำเภอเมืองแพร่ แพร่ 54000', '054-599-209', 'เปิดให้เข้าชมทุกวัน เวลา 06.00-18.00 น.', 'https://web.facebook.com/Watphrathatchohae/?locale=th_TH&_rdc=1&_rdr#', 'https://maps.app.goo.gl/NUKZkafh2AKDxo6c7', 'https://www.google.com/maps/embed?pb=!1m14!1m8!1m3!1d3792.6761528377365!2d100.2021254!3d18.0865408!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x30d8c1d7a684003b%3A0xb2a0129506faff44!2z4Lin4Lix4LiU4Lie4Lij4Liw4LiY4Liy4LiV4Li44LiK4LmI4Lit4LmB4LiuIOC4nuC4o-C4sOC4reC4suC4o-C4suC4oeC4q-C4peC4p-C4hw!5e0!3m2!1sth!2sth!4v1745162988774!5m2!1sth!2sth', 'chorhae', 'travelprogram'),
(2, 'ฮ่อมแม่ก๋อน', 'ร้านอาหารอยู่ท่ามกลางเขา บรรยกาศดี ร่มรื่น มีลำธารข้างๆ ร้านมาวันหยุดคนเยอะมาก อาหารอร่อยมาตรฐาน ปลาหมึกผัเไข่เค็ม อันนี้อร่อยสำหรับใันนี้ • ปลานิลทอดตะไคร้ ตัวใหญ่มากๆ เนื้อปลาไม่ได้มีรสอะไร แต่เด็ดที่น้ำจิ้ม จัดจ้านมาก • ส้มตำปูกลาร้า อร่อยใช้ได้ ไม่หวาน • ตำเส้นขนมจีนสด ขนมจีนแข็งไปนิด ถ้ามีอะไรใส่เพิ่มนอกจากที่มีเส้นอย่างเดียว จะทำให้จานนี้อร่อยขึ้น ตอนนี้รสชาติดีอยู่แล้ว ', '1745163521_hommaekorn.jpg', 'ข้อมูลติดต่อ', 'บ้านนาคูหา หมู่ 5 ตำบลสวนเขื่อน อำเภอเมืองแพร่ จังหวัดแพร่ 54000', ' 096-698-9503', 'เปิดตั้งแต่วันอังคาร เป็นต้นไป เวลา 09.00-18.00 น.', 'https://web.facebook.com/Hommaekorn?locale=th_TH', 'https://maps.app.goo.gl/1oR86seDX1SjiKYBA', 'https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3793.408469969192!2d100.2396174!3d18.052635599999995!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x30d8c74998ded7db%3A0xff07cdbf25d20dec!2z4Liu4LmI4Lit4Lih4LmB4Lih4LmI4LiB4LmL4Lit4LiZ!5e0!3m2!1sth!2sth!4v1745163480390!5m2!1sth!2sth', 'chorhae', 'travelprogram'),
